mapper.update:
update plan
set cusid = #{cusid,jdbcType=INTEGER},
  plan = #{plan,jdbcType=VARCHAR},
  plantime = #{plantime,jdbcType=DATE},
  planback = #{planback,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, cusid, plan, plantime, planback
from plan
where id = #{id,jdbcType=INTEGER}
